#The shebang line 
#!/usr/bin/env python3
# import libraries and interfaces
import rclpy
from rclpy.node import Node
from sensor_msgs.msg import Image

# create a new class that inherits from the rclpy Node class
class ImageCapture(Node):
    def __init__(self):
        # In this class, call the parent constructor with super()
        super().__init__('image_capture')
        self.image_value = None
        self.image_count = 0
        
        # subscribe to camera topic
        self.image_sub = self.create_subscription(Image, '/camera1/image_raw', self.image_callback, 10)
        self.timer = self.create_timer(2.0, self.timer_callback)
        
        self.get_logger().info('Image capture node started, subscribing to /camera1/image_raw')
    
    def image_callback(self, msg):
        # store incoming image data
        self.image_value = msg
        self.image_count += 1
    
    def timer_callback(self):
        if self.image_value:
            # check if objects are visible by examining pixel brightness
            data_sum = sum(self.image_value.data)
            
            # objects visible when brightness is high (within 0.5m)
            if data_sum > 50000000:
                self.get_logger().info('Image is being taken - Objects within 0.5m range')
            else:
                self.get_logger().info('Image is NOT being taken - Objects more than 0.5m away')
        else:
            self.get_logger().info('Waiting for camera data...')

# Create a main() function 
def main(args=None):
    rclpy.init(args=args)  # initialise ROS2 communication  
    node = ImageCapture()  # create an object of the class 
    rclpy.spin(node)  # start Node execution
    rclpy.shutdown()  # shutdown ROS2 communication

# call the main() function 
if __name__ == '__main__':
    main()
